 SUM(CASE WHEN DATE_FORMAT(b.closing_time, '%Y')=#{year} 

 and  DATE_FORMAT(b.closing_time, '%m')=#{month} 

 AND DATE_FORMAT(b.risk_time, '%Y')=#{year}  THEN 1 ELSE 0 END) as totalCount,
 SUM(CASE WHEN DATE_FORMAT(b.closing_time, '%Y')=#{year} 

 and  DATE_FORMAT(b.closing_time, '%m')=#{month}

 AND DATE_FORMAT(b.risk_time, '%Y')=#{year} THEN b.amount_of_money ELSE 0 END)  as sumMoney,
 SUM(CASE WHEN DATE_FORMAT(b.closing_time, '%Y')=#{year} 

 and  DATE_FORMAT(b.closing_time, '%m')=#{month} 

 AND DATE_FORMAT(b.risk_time, '%Y')=#{year} AND b.amount_of_money<=#{moneyType}THEN 1 ELSE 0 END) as totalCountLast,
  SUM(CASE WHEN DATE_FORMAT(b.closing_time, '%Y')=#{year} 

 and  DATE_FORMAT(b.closing_time, '%m')=#{month}

 AND DATE_FORMAT(b.risk_time, '%Y')=#{year}AND b.amount_of_money<=#{moneyType}THEN b.amount_of_money ELSE 0 END)  as sumMoneyLast,
SUM(CASE WHEN DATE_FORMAT(b.risk_time, '%Y')in(#{year},#{year}-1,#{year}-2,#{year}-3) AND DATE_FORMAT(b.closing_time, '%Y')=#{year} 

 and  DATE_FORMAT(b.closing_time, '%m')=#{month} 

AND b.amount_of_money<=#{moneyType} THEN b.duration/24 ELSE 0 END)/(CASE SUM(CASE WHEN DATE_FORMAT(b.risk_time, '%Y')in(#{year},#{year}-1,#{year}-2,#{year}-3)

 and  DATE_FORMAT(b.closing_time, '%m')=#{month} 

 AND DATE_FORMAT(b.closing_time, '%Y')=#{year} AND b.amount_of_money<=#{moneyType}THEN 1 ELSE 0 END) when 0 then 1 else SUM(CASE WHEN DATE_FORMAT(b.risk_time, '%Y')in(#{year},#{year}-1,#{year}-2,#{year}-3)

 and  DATE_FORMAT(b.closing_time, '%m')=#{month} 

 AND DATE_FORMAT(b.closing_time, '%Y')=#{year} AND b.amount_of_money<=#{moneyType}THEN 1 ELSE 0 END) end)as nowCount,
