 AND DATE_FORMAT(b.closing_time, '%m')=#{month}

AND b.amount_of_money<=#{moneyType} THEN b.duration/24 ELSE 0 END )/(CASE SUM(CASE WHEN DATE_FORMAT(b.closing_time, '%Y')=#{year} 

 and  DATE_FORMAT(b.closing_time, '%m')=#{month} 

 AND DATE_FORMAT(b.risk_time, '%Y')=#{year} AND b.amount_of_money<=#{moneyType}THEN 1 ELSE 0 END)when 0 then 1 else SUM(CASE WHEN DATE_FORMAT(b.closing_time, '%Y')=#{year} 

