 AND DATE_FORMAT(b.risk_time, '%Y')=#{year} AND b.amount_of_money<=#{moneyType}THEN 1 ELSE 0 END)end)-SUM(CASE WHEN DATE_FORMAT(b.risk_time, '%Y')=#{year}-1 AND DATE_FORMAT(b.closing_time, '%Y')=#{year}-1 

 and  DATE_FORMAT(b.closing_time, '%m')=#{month}

