 and  DATE_FORMAT(b.closing_time, '%m')=#{month} 

 AND DATE_FORMAT(b.risk_time, '%Y')=#{year}-1 AND b.amount_of_money<=#{moneyType}THEN 1 ELSE 0 END)end) as countYearDayLast,
SUM(CASE WHEN DATE_FORMAT(b.risk_time, '%Y')=#{year} 

 AND DATE_FORMAT(b.risk_time, '%m')=#{month} AND DATE_FORMAT(b.closing_time, '%m') =#{month}

 AND DATE_FORMAT(b.risk_time, '%m')=DATE_FORMAT(b.closing_time, '%m') AND DATE_FORMAT(b.closing_time, '%Y')=#{year} AND b.amount_of_money<=#{moneyType}  THEN b.duration/24 ELSE 0 END)/(CASE SUM(CASE WHEN DATE_FORMAT(b.closing_time, '%Y')=#{year} 

